svg.script:

var elems = [document.getElementById('clip'), document.getElementById('notif')];
var duration = .2;
var tl = new TimelineLite()
          .to(elems, duration, {attr: {rx: '+=5'}})
          .to(elems, duration / 2, {attr: {ry: '+=5'}}, '-=' + duration / 2)
          .to(elems, duration * 3, {attr: {rx: '-=5'}, ease: Elastic.easeOut})
          .to(elems, duration * 2, {attr: {ry: '-=5'}, ease: Elastic.easeOut}, '-=' + duration * 3)
          .pause();
function startAnim () {
  tl.play(0);
}
var button = document.getElementById('mail');
button.addEventListener('click', startAnim);
